import React, { useState } from 'react';
import axios from 'axios';
import './components/uploadpdf.css';

const UploadPDF = () => {
  const [files, setFiles] = useState([]);
  const [key, setKey] = useState('');
  const [message, setMessage] = useState('');
  const [isSending, setIsSending] = useState(false);

  const handleFileChange = (event) => {
    setFiles(event.target.files);
  };

  const handleKeyChange = (event) => {
    setKey(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (files.length === 0) {
      setMessage('Please select at least one file.');
      return;
    }

    const formData = new FormData();
    formData.append('key', key);

    for (let i = 0; i < files.length; i++) {
      formData.append('files', files[i]);
    }

    try {
      setIsSending(true);
      setMessage('Please wait while we process the documents.');

      const response = await axios.post('http://localhost:8000/api/store-documents/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      console.log(response.data)
      setIsSending(false);
      setMessage(`Successfully stored documents. Key: ${response.data.key}`);
    } catch (error) {
      setMessage('Error storing documents.');
      console.error(error);
    }
  };

  return (
    <div className="upload-container">
      <h2>Upload PDF and Key</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="keyInput" disabled={isSending}>Key:</label>
          <input type="text" id="keyInput" value={key} onChange={handleKeyChange}/>
        </div>
        <div>
          <label htmlFor="fileInput" disabled={isSending}>PDF Files:</label>
          <input type="file" id="fileInput" onChange={handleFileChange} accept=".pdf" multiple required />
        </div>
        <button type="submit" disabled={isSending}>
          {isSending ? 'Please Wait' : 'Upload'}
        </button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default UploadPDF;
