//import React, { useState } from 'react';
import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import './components/chatbot.css';

function Chatbot() {
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([
    { message: 'Hi, welcome to ProChat! Go ahead and send me a message. ðŸ˜„', isUser: false }
  ]);

  const [isSending, setIsSending] = useState(false);

  const chatContainerRef = useRef(null); // Ref for the chat container element

  // Function to scroll chat to the bottom
  const scrollToBottom = () => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  };

  // Effect to scroll to bottom when chat updates
  useEffect(() => {
    scrollToBottom();
  }, [chat]);


  const sendMessage = async (e) => {
    e.preventDefault();
    if (message.trim() === '') return;

    // To disable sending button while sending
    setIsSending(true);

    // Add user's message to the chat
    setChat((prevChat) => [...prevChat, { message, isUser: true }]);

    try {
      // Send a POST request to the chatbot API with the user's message
      const response = await axios.post('http://localhost:8000/chatbot/', { message });

      // Add the chatbot's response to the chat
      setChat((prevChat) => [...prevChat, { message: response.data.message, isUser: false }]);
    } catch (error) {
      console.error('Error sending message:', error);
    }

    // Clear the message input field
    setMessage('');

    // Enable sending button after message is sent
    setIsSending(false);
  };

  return (
    <section className="msger">
      <header className="msger-header">
        <div className="msger-header-title">
          <i className="fas fa-comment-alt"></i> ProChat
        </div>
        <div className="msger-header-options">
          <span><i className="fas fa-cog"></i></span>
        </div>
      </header>

      <main className="msger-chat">
        {chat.map((chatMessage, index) => (
          <div key={index} className={`msg ${chatMessage.isUser ? 'right-msg' : 'left-msg'}`}>
            <div
              className="msg-img"
              style={{ backgroundImage: `url(${chatMessage.isUser ? 'https://image.flaticon.com/icons/svg/145/145867.svg' : 'https://image.flaticon.com/icons/svg/327/327779.svg'})` }}
            ></div>

            <div className="msg-bubble">
              <div className="msg-info">
                <div className="msg-info-name">{chatMessage.isUser ? 'You' : 'BOT'}</div>
              </div>

              <div className="msg-text">
                {chatMessage.message}
              </div>
            </div>
          </div>
        ))}
      </main>

      <form className="msger-inputarea" onSubmit={sendMessage}>
        <input
          type="text"
          className="msger-input"
          placeholder="Enter your message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <button type="submit" className="msger-send-btn">Send</button>
      </form>
    </section>
  );
}

export default Chatbot;
