import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'; // Import Link for navigation
import Botlogo from './bot.avif';
import Userlogo from './user.png';
import './chatbot.css';

function Chatbot() {
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([
    { message: 'Hi, welcome to ProChat! Go ahead and send me a message. ðŸ˜„', isUser: false }
  ]);
  const [isSending, setIsSending] = useState(false);
  const [key, setKey] = useState('');
  const [isKeyValid, setIsKeyValid] = useState(false);
  const [keyError, setKeyError] = useState('');
  const [showModal, setShowModal] = useState(true); // Show the modal by default

  const chatEndRef = useRef(null);

  const verifyKey = async () => {
    try {
      // Replace with your actual endpoint for key verification
      setKeyError('');
      const response = await axios.post('http://192.168.13.203:8000/api/verify-key/', { key });
      if (response.data.valid) {
        setIsKeyValid(true);
        setShowModal(false); // Hide the modal on successful verification
        setKeyError('');
      } else {
        setKeyError('Your key does not match. Please enter the key again or go back.');
      }
    } catch (error) {
      console.error('Error verifying key:', error);
      setKeyError('An error occurred while verifying the key. Please try again.');
    }
  };

  const goBack = () => {
    window.history.back();
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (message.trim() === '' || isSending) return;

    setIsSending(true);
    setChat((prevChat) => [...prevChat, { message, isUser: true }]);

    const eventSource = new EventSource(`http://192.168.13.203:8000/api/chatbot-stream/?message=${message}&key=${key}`);

    eventSource.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setChat((prevChat) => [...prevChat, { message: data.message, isUser: false }]);
      scrollToBottom();
    };

    eventSource.onerror = (error) => {
      console.error('Error receiving streamed data:', error);
      eventSource.close();
    };

    eventSource.onopen = () => {
      setIsSending(false);
      setMessage('');
    };
  };

  useEffect(() => {
    scrollToBottom();
  }, [chat]);

  const scrollToBottom = () => {
    if (chatEndRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section>
      {/* Home button */}
      {showModal ? (
        <div className="modal">
          <div className="modal-content">
            <h2>Enter Organization Key</h2>
            <input
              type="text"
              className="key-input"
              placeholder="Enter your organization key..."
              value={key}
              onChange={(e) => setKey(e.target.value)}
            />
            <div className="modal-buttons">
              <button onClick={goBack} className="modal-button">Go Back</button>
              <button onClick={verifyKey} className="modal-button">Verify Key</button>
            </div>
            {keyError && <p className="key-error">{keyError}</p>}
          </div>
        </div>
      ) : (
        <>
          <Link to="/" className="home-button">Home</Link>
          <div className='msger'>
            <header className="msger-header">
              <div className="msger-header-title">
                <i className="fas fa-comment-alt"></i> ProChat
              </div>
              <div className="msger-header-options">
                <span><i className="fas fa-cog"></i></span>
              </div>
            </header>

            <main className="msger-chat">
              {chat.map((chatMessage, index) => (
                <div key={index} className={`msg ${chatMessage.isUser ? 'right-msg' : 'left-msg'}`}>
                  <div
                    className="msg-img"
                    style={{ backgroundImage: `url(${chatMessage.isUser ? Userlogo : Botlogo})` }}
                  ></div>

                  <div className="msg-bubble">
                    <div className="msg-info">
                      <div className="msg-info-name">{chatMessage.isUser ? 'You' : 'BOT'}</div>
                    </div>

                    <div className="msg-text">
                      {chatMessage.message}
                    </div>
                  </div>
                </div>
              ))}
              <div ref={chatEndRef}></div>
            </main>

            <form className="msger-inputarea" onSubmit={sendMessage}>
              <input
                type="text"
                className="msger-input"
                placeholder="Enter your message..."
                value={message}
                disabled={isSending}
                onChange={(e) => setMessage(e.target.value)}
              />
              <button type="submit" className="msger-send-btn" disabled={isSending}>
                {isSending ? '...' : 'Send'}
              </button>
            </form>
          </div>
        </>
      )}
    </section>
  );
}

export default Chatbot;
